@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
url-prefix("http://www.google.com/reader/view/"),
url-prefix("https://www.google.com/reader/view/")
{
*:focus 
{outline:none !important}

html,
body
{color:#333 !important;
/*font-family:"sans-serif" !important;*/}

#top-bar
{height: 0px !important}

#gb,
#gbar,
#guser,
#chrome-header,
#viewer-footer,
#logo-container,
#viewer-single-parent
{display:none !important}

#main
{top:0 !important;
font-size:110% !important;}

#viewer-details-toggle
{float:left !important}

#search
{top:5px !important;
left:auto !important;
right:5px !important;}

.item-body
{line-height:200%}

#search-input
{-moz-border-radius:5px}

#chrome-view-links
{right:400px !important}

.lhn-section,
.scroll-tree li,
.goog-button-base-inner-box,
#viewer-header,
#viewer-footer,
#chrome-lhn-toggle,
#lhn-add-subscription-section
{background:#DDE9F8 !important}

.entry-date
{margin-right:1em !important}

