css:
@-moz-document url-prefix('http://www.douban.com/'), url-prefix('http://movie.douban.com/'), url-prefix('http://music.douban.com/'), url-prefix('http://book.douban.com/'){

body,td,th {font-size:14px;font-family: "Helvetica Neue", "Luxi Sans", "DejaVu Sans", Tahoma, "Hiragino Sans GB", STHeiti;}

body {background-image: url("http://static.v2ex.com/grids/purple.png");}
#wrapper a:hover{background:none repeat scroll 0 0 #3366CC;color:#fff!important;text-decoration:none;padding:2px;}
#wrapper a{-webkit-border-radius:2px;padding:2px;}

#content span::selection,#content p::selection,#content h1::selection,#content h2::selection,#content h3::selection,#content h4::selection{background:#51a201 none repeat scroll 0 0;color:#fff;}

#content,#header,#footer,#wrapper > h1,#db-usr-profile{background-color: #FFFFF5;-webkit-border-radius: 5px;border: 2px solid rgba(0, 0, 0, 0.15);}

.top-nav .bd {background-color: #FFFFF5;}

#content {padding:10px;margin-top:-4px;}
#header,#footer{padding:10px 2px;}

#wrapper {width: 970px;}

#wrapper > h1, #db-usr-profile{border-bottom-right-radius:0 0;border-bottom-left-radius:0 0;border-bottom:none;padding:10px;}
#db-usr-profile {margin-bottom:0;width:auto;}
.article #db-usr-profile {border-radius:0 0;border:0;}

.top-nav {background: -webkit-gradient(linear, left top, left bottom, from(#444), to(#111));border-bottom:none;height:36px;}
.top-nav a:link,.top-nav a:visited{color:#ccc;}
.top-nav-items {font-size: 14px;}

.movie_top ul li {line-height: 16px;}

#icp {display: none;}

input[type=submit] {padding:4px 8px;}
input[type=text] {height:16px;font-size:14px;}
textarea {outline:none;resize: both;font-size:14px;}
}
