css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("minecraftwiki.net") {
  #mw-js-message + center, #bodyContent + center, #content + center {
    display: none !important;
  }
}

@-moz-document domain("instacalc.com") {
  iframe[name="google_ads_frame"] { display: none !important; }
  div#main-content + div { display: none !important; }
}

@-moz-document domain("userstyles.org") {
  div.ad {
    display: none !important;
  }
}

@-moz-document domain("steamcard.com") {
  div.ad-content { display: none !important; }
  div.ad-side { display: none !important; }
}

@-moz-document domain("timeanddate.com") {
  iframe { display: none !important;}
}

@-moz-document domain("mygamercard.net") {
  html>body>div#container>div#content>div>object, span#abpPopC + center, .miniboxalt, .miniboxalt2, div#content>center:first-child, span#abpPopC + div[style="text-align: center"] {
    display: none !important; 
  }
}

@-moz-document domain("alternativeto.net") {
  .divLeaderboardHeaderLove { display: none !important; }
  #logotype { height: 100px; }
  div[style="position: relative; left: -4px"] {
    display: none !important;
  }
}
