css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joindiaspora.com"), domain("diasp.org"), domain("geraspora.de") {
/* PLEASE REPLACE JOINDIASPORA.COM WITH THE DOMAIN NAME OF YOUR POD ABOVE if not in the list^ */


/* --------Header-------- */
header{
background:-moz-linear-gradient(center bottom,#fff 1%,#800000 12%) !important;
background:-webkit-gradient(linear,0% 0,0% 100%,from(#800),to(#fff),color-stop(0.9,#800));
}

/* --------User Menu-------- */
#user_menu {
background-color:#555 !important;
-moz-box-shadow:0 1px 2px #000000 !important;
}

#user_menu.active{
background-color:#555 !important;
-moz-box-shadow:0 1px 2px #000000 !important;
}

#user_menu.active a:hover{
background-color:#800 !important;
}

/* --------Aspect tabs-------- */
#aspect_nav{
margin-bottom:2px !important;
}

#aspect_nav a:hover {
background-color:#fff !important;
color:#333 !important;
text-shadow: #fff 0px -0px 4px !important;
}

/* --------Links on login page-------- */
#landing_nav a {
color:#fff !important;
}

}
