css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joindiaspora.com"), domain("diasp.org"), domain("geraspora.de") {
/* PLEASE REPLACE JOINDIASPORA.COM WITH THE DOMAIN NAME OF YOUR POD ABOVE if not in the list^ */

/* ------General colours------ */
header{
background:-moz-linear-gradient(center bottom,#fff 1%,#088 12%) !important;
background:-webkit-gradient(linear,0% 0,0% 100%,from(#088),to(#fff),color-stop(0.9,#088));
}


/* ------User Menu------ */
#user_menu {
background-color:#cef !important;
-moz-box-shadow:0 1px 2px #000 !important;
}

#user_menu, #user_menu a{
color:#000 !important;
text-shadow: #ccc 0px -0px 4px;
}

#user_menu.active{
background-color:#cef!important;
-moz-box-shadow:0 1px 2px #000 !important;
}

#user_menu.active a{
color:#000 !important;
text-shadow: #ccc 0px -0px 4px;
}

#user_menu.active a:hover{
background-color:#088 !important;
color: #eee !important;
}

/* ------Aspect tabs------ */
#aspect_nav{
margin-bottom:2px !important;
}


#aspect_nav a{
color: #fff !important;
} 

#aspect_nav .selected a{
color: #000 !important;
}

#aspect_nav a:hover {
background-color:#fff !important;
color:#355 !important;
text-shadow: #fff 0px -0px 4px !important;
}

/* ------ Search bar ------ */
#global_search form input{
background-color:#ddd !important;
}

#global_search form input:hover{
background-color:#d0e5ff !important;
}

#global_search form input.active{
background-color:#eee !important;
}

/* ------Links on logine page------ */
#landing_nav a {
color:#fff !important;
}

}
