css:
@-moz-document domain(informatik-forum.at) {
  body, .page  {
    background-color: #abb !important;
  }
  #posts a:visited,
  #threadslist a:visited {
    color: #033 !important;
  }
  #posts a:hover,
  #threadslist a:hover {
    color: #f40 !important;
  }
  body > table {
    display: none;
  }
}
