css:
body { font-family: tahoma !important; }

.text-area-editor textarea{
font-family: tahoma !important;
font-size: 11px !important;
text-align: right;
direction: rtl;
}

.main-content { 
width: 630px !important;  }

.text-area .text-area-editor .twitter-anywhere-tweet-box-editor {
width: 570px !important;  }

.tweet-image {
float: right !important;
}

.tweet-content {
margin-right: 58px !important;
margin-left: 0 !important;
}

.tweet-user-name {
margin-right: 10px !important;
font-size: 11px !important;
}

.tweet-text {
font-family: tahoma !important;
font-size: 11px !important;
padding: 2px 0 !important;
direction: rtl;
text-align: right;
}

.protected-icon-timeline {
margin:0 0 0 5px !important;
}

._timestamp {
float: left;
direction: ltr;
}

.tweet-actions {
direction: ltr;
}

.dashboard {
background-color: #252525;
max-width: 250px !important;
min-width: 250px !important;
font-size: 11px !important;
font-family: tahoma !important;
}

.component h2 span.user-stat-link { color: #666666 !important; }

div.recently-listed-in ul.recent-lists, .dashboard ul, .dashboard ul li, .base-list, .base-list li, .inline-list, .inline-list li a, .trend-toggle a, .footer p a{ color: #666666 !important; } 
.dashboard-component-title { 
font-size: 13px !important;
font-weight: bold; }


.dashboard .tweet-activity{
height: 100px !important;
}

.dashboard .tweet-image, .dashboard .tweet-image img{
width: 53px !important;
height: 53px !important;
}

.dashboard .tweet-content{
height: 100px !important;
}

.dashboard .tweet-content .tweet-row{
height: 100px !important;
}

.dashboard .tweet-content .tweet-row .tweet-text{
font-size: 12px !important;
color: #6E6E6E;
text-align: right;
direction: rtl;
height: 100px !important;
float: right !important;
margin-top: -15px !important;
margin-right: 3px !important;
width: 190px !important;
}

.dashboard .tweet-content .tweet-row .latest-tweet-timestamp, .dashboard .component .user-rec-inner {
font-size: 5px !important;
display: none;
}

.your-activity {
  width: 100% !important;
  border-top: 1px solid #656565;
  margin-top: 5px;
  margin-bottom: 5px;
  height: auto !important;
}

.following-activity {
  border: none;
}

.latest-favorite, .tweet-text {
width: 100% !important;
}

.latest-favorite .tweet-text {
float: right;
}

.pane-components .tweet-row .tweet-text{ 
font-size: 11px !important;
font-family: tahoma !important;
}
