@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(nirsoft.net) {

/* Hides Add This Ad */
.addthis_button, a[href*="addthis.com"] { display: none !important }

/* Hides Capsa Ad */
a[href*="colasoft.com"] { display: none !important }

/* Hides Ads In Footer */
a[href*="brothersoft.com"], a[href*="snapfiles.com/"], a[href*="sofotex.com/"]  { display: none !important }

}
