css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("democracynow.org") {

#site_wrapper { width: auto !important; }

#wrapper_left { width: auto !important; }

#logo { display: none !important; }
#button_container { padding-top: 0 !important}
#button_container > a { display: none !important; }
#header_text { display: none !important; }
#top_nav { margin-top: 0 !important; }

div.story > h1 { float: none !important; }

.red_box { display: none !important; }
.share_box { display: none !important; }
div.intro { display: none !important; }

div.segment_options { display: none !important; }
div.taggable { display: none !important; }
#segment_media { padding: 0 !important; }
#segment_media table td { height: auto !important; }
#segment_media > table > tbody > tr > td.labels { display: none !important; }
#story_meta > p { display: none !important; }
.formats p { margin: 0 !important; }
a.real_video { display: none !important; }
a.real_audio { display: none !important; }
div.formats > p { float: left !important; }
div.formats > br { display: none !important; }
div.formats { margin: 0 !important; }
}
