@-moz-document domain('youtube.com') {

div#watch-discussion { display:none!important; }
div#watch-sidebar { display:none!important; }
div#pagebottom { display:none!important; }
div#watch-player { height: 720px !important; }
div#watch-player embed { height:720px!important; width:1227px!important; }
#masthead, #masthead-subnav ul, .watch-headline, #watch-headline, #watch-video, #watch-main, #watch-search { width:1227px!important; }
div#watch-info { width:1227px!important; }
div#watch-actions { width:1227px!important; }

}
