@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.windowsitpro.com/article/), url-prefix(http://www.windowsitpro.com/blogs/) {

/* Header */
#Header { display: none !important }

/* Top Menu */
.BreadcrumbBox { display: none !important }

/* White Background */
#PageWrapper { background: #FFFFFF !important }

#Content { margin-top: -120px !important }

/* Left Side-Bar */
.LeftCol { display: none !important }

/* Right Side-Bar */
.RightCol, #dnn_RightPane { display: none !important }

/* Share this Post */
.share-block { display: none !important }

/* Comments */
#divAddComments, .addComment, .comment { display: none !important }

/* Footer */
#FooterBottom { display: none !important }

/* "Rate It" */
#lxTdivRating { display: none !important }

/* "CopyRight Link" */
.contentItem { display: none !important }

/* "You must log on before posting a comment" */
#dnn_ctr492_ContentPane { display: none !important }

}
