css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cbc.ca") {

#gniframe {height:43px !important;}

/*** Phasing out #left {top: 43px !important;} ***/
#leftnav {margin-top: 16px !important;}

/*** Ads ***/
#advertisement, .ad-300x250, .ad-160x600, .ad_box, .box300_250_banner, .box-ad, .google_ads, #advert300x250, #masthead-ad.ad-728x90, .bigbox, .fullbar, #storybody hr, #FLASH_AD, #adbar, #gnadww, #gn-adww, #bigbox, #googleads, #sponsors, .holiday_guide_header {display: none !important ;}

img *[src*="doubleclick."] { display: none !important; }
#gnwrap {height:inherit !important;}

/*** Weather Applet ***/
#gn-mycbc {display: none !important;}
#globalnav {height:auto !important;}

/*** One Million Acts of Green ***/
*[src*="http://www.cbc.ca/includes/globalnav/v07b/ext.html?gnt,onemillionactsofgreen"] {height: 30px !important;}

/*** Radio 1 Background Moves ***/
#page_main {background-position: center -110px !important;}
}

@-moz-document url-prefix("http://www.cbc.ca/stevenandchris/") {
#site_header_image {margin-bottom: 77px !important;}
}
