css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("odb.org") {

div.readable-area, -wrapper post-type-wrapper.module-header{
background-color:#D1C9B7 !important;
border: 1px solid #aaa !important;
}
.listen-now-box{
border: 2px solid #fff !important;
padding-top: 5px !important;

}
#article-detail{
background-color:#EDE49A !important;
border: 2px solid #fff !important;
}
.odb-box{
border-bottom: 0px !important;
}

#article-detail a{
color: #CD7A38 !important;
}

.host-info, .grid-row a{
color:#555 !important;
}

div.post-content{
font-size: 16px !important;
font-weight:bold !important;
font-family: Avenir-Heavy, eurostile, Arial !important;
line-height:140%!important;
color:#555 !important;
font-weight:bold !important;
}
}
