css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cbslocal.com") { /* userstyles */

/* standard text */
ul.blogroll li,
.photo p,
.feature-text p {
color:#222222 !important;
}

/* generated info */
.feature-text .meta,
.comment-stamp {
color:#5C707C !important;
}

/* input text */
.fauxfb_hide_on_login label input,
textarea#comment {
color:maroon !important;
}

/* input labels */
.fauxfb_hide_on_login label {
color:navy !important;
}

}
