@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.de") {
  body{
    background: white !important;
  }

  font, .q, a{
    font-family: Verdana !important;
  }

  img[src*="images/hp0.gif"], img[src*="/images/logo.gif"]
  {
    width: 0 !important;
    padding-left: 350px !important;
    height: 110px !important;
    background: url("http://img160.imageshack.us/img160/9308/googlecodejunkiebu2.gif") no-repeat !important;
    margin-top: 18px !important;
  }

  img[src*="/images/images_hp.gif"] {
    width: 0 !important;
    padding-left: 350px !important;
    height: 110px !important;
    background: url("http://img160.imageshack.us/img160/9308/googlecodejunkiebu2.gif") no-repeat !important;
    margin-top: 18px !important;
  }

  td[valign="top"][class="h"] {
    width: 0 !important;
    padding-left: 350px !important;
    height: 110px !important;
    background: url("http://img160.imageshack.us/img160/9308/googlecodejunkiebu2.gif") no-repeat !important;
    margin-top: 18px !important;
  }

 img[src*="images/hp1.gif"], img[src*="images/hp2.gif"], img[src*="images/hp3.gif"]
 {
    display: none !important;
  }

}
