css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digg.com") {

#contents {
    margin-top: 0px !important;
    width: 100% !important;
}

#footer {
    display: none !important;
    margin-top: 0 !important;
}

html {font-size: 22pt !important;}
.digg-count, .digg-count span {font-size: 12pt !important; }



div[class="copyright"],
div[class*="news-buried"],
div[id="announce-invite"],
div[class*="_ad"],
a.more {
    display: none !important;
}


/* Hide anything on the side bar but the Top 10 List. */
.side-container:first-child {
    display: none !important;
}

.side-container .news-summary {
    display: block !important;
}

.side-container  {
    display: none !important;
}

#join-digg, .selector, .digg-it, .share, .bury, .user-info, .tool, #header, .submenu-drop, #sub-nav, .news-full .news-details dl, .tab, .c-bot-reply-notice {display: none !important;}
}
