css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("board.ogame.ru"), domain("board.ogame.org"), domain("board.ogame.de") {

#footer, #header, #main, #mainMenu, #userPanel {
    width: auto !important;
    max-width: 80% !important;
}

.messageLeft, .messageRight, .messageFramedLeft, .messageFramedRight, .messageTop, .messageFramedTop {
    background-color: #DDDDDD !important;
}

#footer, #header, .attachmentFile ul li, .attachmentPreview ul li:hover, .avatarDisplay div, .avatarSelect li label, .border, .footerMenu li, .footerOptions li, .galleryIndex li.floatedElement:hover, .inlineCalendarFooter, .inlineCalendarHeader, .inlineCalendarTable, .mainMenu, .marked, .mceResizeBox, .message, .messageContentInner, .messageNumber, .pageNavigation li ul, .pmEditFolders, .pmUsageBar, .popupMenu, .quoteBox, .searchContainer, .subTabMenu, .tabMenu li, .userAvatar a, .userAvatarFramed a, fieldset {
    border-color: #AAAAAA !important;
}

.quoteBox {
    background-color: #DDDDDD !important;
}

.message, .messageInner, .quoteBox, fieldset, .info, .success, .warning, .error, .help, #userCard, .userCardInner, .second .border, .second .border .content, .avatarSelect li.container-4, .avatarSelect li label, .floatedElement, .popupMenu, .loginPopup, .loginPopup .container-1 {
    -moz-border-radius: 5px 5px 5px 5px;
}

}
