css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

#navigator-toolbox::after {
	background-color: rgba(0,0,0,.35) !important;}

#main-window[sizemode="normal"][tabsontop="true"] #nav-bar {
	border: 1px solid rgba(0,0,0,.35) !important;
	border-bottom: none !important;}

#browser-border-start, #browser-border-end {
	display: none !important;}

#main-window[sizemode="normal"] #browser {
	-moz-appearance: none !important;
	border: 1px solid rgba(0,0,0,.35) !important;
	border-top: none !important;
	border-bottom: none !important;}

#main-window[sizemode="normal"][tabsontop="true"] #PersonalToolbar {
	border-color: rgba(0,0,0,.35) !important;}

#main-window[sizemode="normal"][tabsontop="false"] #PersonalToolbar {
	border: 1px solid rgba(0,0,0,.35) !important;
	border-bottom: none !important;}

#main-window[sizemode="maximized"][tabsontop="false"] #PersonalToolbar {
	border-top: 1px solid rgba(0,0,0,.35) !important;}

#main-window[sizemode="normal"][tabsontop="false"] #PersonalToolbar[collapsed="false"] + #TabsToolbar {
	margin-top: 0px !important;
	padding: 0 1px !important;
	padding-left: 2px !important;
	border-left: 1px solid rgba(0,0,0,.35) !important;
	border-right: 1px solid rgba(0,0,0,.35) !important;}

.tabbrowser-tab, .tabs-newtab-button {
	-moz-border-image: url("chrome://browser/skin/tabbrowser/tab.png") 4 3 2 3 / 4px 2px 3px 2px repeat stretch !important;}
