css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pages.google.com") {
	/* page item highlight */
	table.tr_page div>div:hover * {
		background: rgb(255,235,134) !important;		
	}
	table.tr_page-in-revision-state-3 div>div:hover * {
		background: rgb(255,235,134) !important;		
	}

	/* file item highlight */
	#tr_file-view-tbody>tr:hover *{
		background: rgb(255,235,134) !important;
		color: #000 !important
	}

	/* replace trash icon and add hover for it*/
	img[src="/-/images/trash.gif"] {
		width:0px !important;
		height:0px !important;
		padding:0px !important;
		padding-top:13px !important;
		padding-left:13px !important;
		background-image: url("data:image/gif;base64,R0lGODlhDQANAJEDAP///8zMzJmZmf///yH5BAEAAAMALAAAAAANAA0AAAInnIUpxiMA4Tqqqoix3fMF8D3TAZZi45nA6JThirorzKQv+2QBzhkFADs=") !important;
	}
	#tr_file-view-tbody>tr>td:last-child>img {
		background-image: url("data:image/gif;base64,R0lGODlhDQANAJEDAP///8zMzJmZmf///yH5BAEAAAMALAAAAAANAA0AAAInnIUpxiMA4Tqqqoix3fMF8D3TAZZi45nA6JThirorzKQv+2QBzhkFADs=") !important;
	}
	#tr_file-view-tbody>tr>td:last-child>img:hover {
		background-image: url("data:image/gif;base64,R0lGODlhDQANAKIFAP/MzP+Zmf9mZv8zM/8AAP///wAAAAAAACH5BAEAAAUALAAAAAANAA0AAAMqWErcpLAQQOlbLrvKuf7XJADjEFwLqZqopJZnJK4xNMPtDbByF9SYjyIBADs=") !important;
	}
}
