css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("channelate.com")
{

.navi {
  position:relative;
  z-index:2
}
.navi-next {
  height:100%;
  position:absolute;
  z-index:1;
  display:block;
  margin-left:-250px;
  width:600px;
  background-position:544px 0px;
}
.navi-void, .navi-void:hover {
  position:relative;
  margin-left:3px;
  width:63px;
  height:25px;
  background-position:-63px 0px;
}
.navi-void:hover + .navi-last, .navi-void + .navi-last {
  margin-left:0px;
}
.navi-last {
  margin-left:72px;
}
#content-wrapper {
  position:relative;
  z-index:3;
}


}
