@namespace url(http://www.w3.org/1999/xhtml);

/* Canada */

@-moz-document domain("blogto.com") {#comments {display: none !important;} .addcomment {display: none !important;}}
@-moz-document domain("canada.com") {.make-comment {display: none !important;}}
@-moz-document domain("cbc.ca") { #commentwrapper {display:none !important;} }
@-moz-document domain("ctv.ca") { .most-viewed,.most-viewed-content {display:none !important;}}
@-moz-document domain("ctvnews.ca") { .articleComments, .commentList {display:none !important;}}
@-moz-document domain("dailyxtra.com") {#comments,.views-field-comment-count,.block-views-comment-block, .block-comment-block, .views-comment, .views-field-comment-body, .comment-signin-login, .main-story-side .publish-date .field-content {display: none !important;}}
@-moz-document domain("nationalpost.com") {#disqus_thread, a.dsq-brlink, .npIconComments {display:none !important;}}
@-moz-document domain("nowtoronto.com") {#comments, #disqus_thread, #mostWidget {display: none !important;}}
@-moz-document domain("theglobeandmail.com") { .sm-comments,.cmt,.comment-info,#user-comments {display: none !important;} }
@-moz-document domain("thegridto.com") {#comments, .article-comment {display: none !important;}}
@-moz-document domain("thestar.com") { #article-comments, .your-comments {display:none !important;} }
@-moz-document domain("torontosun.com") {.comments {display: none !important;}}
@-moz-document domain("winnipegfreepress.com") { #comments {display: none! important;}}

/* U. K. */

@-moz-document domain("dailymail.co.uk") {.comments-link, #sharedLinkTopComment, #addCommentButton, #reader-comments, .article-reader-comments {display: none !important;} }
@-moz-document domain("guardian.co.uk") {a.trail-comment-count, .comment-count, .content-comment-count, #d2-root {display:none !important;} }
@-moz-document domain("independent.co.uk") {#disqus_thread, .dsq-widget-meta, .gotocomments, .disqus {display: none !important;} }

/* U. S. */

@-moz-document domain("cnn.com") {#dsq-content {display: none !important;} }
@-moz-document domain("huffingtonpost.com") {.comments {display: none !important;} }
@-moz-document domain("latimes.com") {#gallery-subcontent, #message-guidelines {display: none !important;} }
@-moz-document domain("news.yahoo.com") { #yom-comments { display: none !important; } } /* Added 12/08/01 thanks to commenter grom! */
@-moz-document domain("nytimes.com") {#commentsContainer {display: none !important;}}
@-moz-document domain("wsj.com") { .comments, #leaveComment, .leaveComment {display: none !important;}}

/* Shut up, I bet you spend too much time here too */

@-moz-document domain("cracked.com") { #Comments {display: none !important;} }
