css:
input.s {
	font-size: 120%;
	margin-left: 0.5px;
	width: 98%;
	background-color: #ddd;
	letter-spacing: -1px;
}

input.tbtn {
	font-size: 120%;
	text-transform: lowercase;
	height: 0;
	width: 0;
	border-style: none;
}

span.o a {
	font-size: 110%;
}

div.tbtn {
	height: 0;
	width: 0;
	border-style: none;
}

input.s:focus {
	background-color: #fff;
	font-size: 185%;
	letter-spacing: -2px;
}

input.s:hover {
	background-color: #eee;
}

p.menu {
	background-color: #ddd;
	width: 100%;
	position: fixed;
	top: 0;
	border-style: solid;
	border-color: #000;
	border-width: 0;
	border-bottom-width: 1px;
	font-size: 150%;
}

body {
	margin-top: 30px;
	font-size: 90%;
}

p.menu a {
	display: inline-block;
}

p.menu a:hover {
	background-color: #bbb;
	color: #fff;
}

span.o a:hover {
	background-color: #000;
	color: #fff;
}

li p a, p small a, p.bt a {
	padding-left: 1px;
	padding-right: 1px;
}

li p a:hover, p small a:hover, p.bt a:hover {
	background-color: #60d167;
	color: #ffffff;
	padding-left: 1px;
	padding-right: 1px;
}

div.l {
	display: none;
}

a.il:hover, a.i:hover {
	background-color: #de4545;
	color: #fff;
}

a.b:hover {
	background-color: #eb2323;
	color: #ffffff;
}
