css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.youtube.com/"), url-prefix("http://www.youtube.com/index") {
#baseDiv, #footer-container, #quicklist, #masthead-utility, #content-container,
    #masthead-nav, #masthead-user-bar-container
        { display: none !important; }

#page { position: relative !important; top: 40% !important; }
#masthead > form, #masthead > a > img, #masthead > div, #masthead > a { float: none !important; margin-left: auto !important; margin-right: auto !important; }
#masthead { width: auto !important; text-align: center; }
#logo-container, #masthead-search-bar-container { display: inline-block !important; }
#masthead-search-bar-container { width: 30% !important; }
}

@-moz-document url-prefix("http://www.youtube.com/results?") {
#search-pva-content, .yt-video-box.playlist, #footer-container, .yt-alert, .search-related,  #masthead-utility { display: none !important; }
#results-main-content { width: 100% !important; }
.video-main-content { width: 50% !important; }
}

@-moz-document url-prefix("http://www.youtube.com/watch?") {
#masthead-utility, #watch-branded-actions, #footer-container, #watch-sidebar,
    #watch-actions-right ~ *:not(:last-child), #watch-insight-button, .subscription-container, #watch-video-count,
    #watch-headline-user-info, #watch-transcript, #watch-flag, .comments-section ~ *, #watch-description-extra-info
        { display: none !important; }
#watch-actions-right { width: 100% !important; text-align: center !important; }
#content, .first, .second, .third { background: transparent !important; }
#eow-title { color: black !important; }
#watch-username *, #eow-title > a { color: blue !important; text-decoration: underline !important; }
#watch-info { z-index: 1000 !important; }
#watch-video-container, #watch-video { width: 100% !important; background-color: transparent !important; }
#watch-video *, #watch-main-container * { margin-left: auto !important; margin-right: auto !important; }
#watch-headline-container { text-align: center !important; }
#watch-panel { float: none !important; }
#masthead { width: 50% !important; }
#watch-info { min-height: 75px; }
#watch-description { min-height: 70px; }
div.yt-uix-expander-collapsed > #watch-description-toggle { top: 30px; }
#watch-video-container { background: transparent !important; }
}
