css:
@-moz-document domain('slashdot.org') {

header.h { position: absolute ! important }
.view_mode, .commentBox, div.col_1 { display:none !important; }
div.col_2 { margin-left:-120px !important; }

#commentwrap { display: block !important; visibility: visible! important }
.commentBody { padding: 5px 20px 0px !important }
.commentSub { position: absolute; z-index:10;  opacity: 0; -webkit-transition: opacity 1s 1s; background-color: white; padding: 10px; border-radius: 5px }
:hover > .commentSub { opacity: 1 }

}
