css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("play.quarantine2019.com") {

	div#header{
		visibility: hidden !important; 
		width: 0 !important; 
		height: 0 !important;
	}
	HTML > BODY > DIV:first-child + SCRIPT + SCRIPT + SCRIPT + DIV{
		visibility: hidden !important;
		/* height: 0 !important; wtf */
	}
	HTML > BODY > DIV:first-child + SCRIPT + SCRIPT + SCRIPT + DIV > DIV:last-child 
	{
		height: 0 !important;
		visibility: hidden !important;
	}
	div#wrapper {
		position: absolute;
		top: 10px !important;
		left: 10px !important;
	}
	#pagecontent > CENTER {
		position: absolute;
		top: 90%;
                left: -165px;
	}
}
