css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.df.eu/forum/") {

   .df-main-header-nav-wrap {
      display: none;
   }
   
   #vbtab_forum, #vbtab_whatsnew {
      display: none;
   }
   
   #top_header {
      margin-bottom: 15px;
   }
   
   #middle_header {
      height: 50px;
   }
   
   .logo-image {
      bottom: 8px;
      left: 8px;
   }
   
   #navtabs ul.floatcontainer {
      top: -5px;
   }
   
   #thread_info {
      display: none;
   }
   
   .df-footer {
      display: none;
   }
}
