css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("jsfiddle.net") {
fieldset.column.left {
  width: 1px !important;
}

fieldset.column.right > div.window.top {
  height: 1px !important;
}

fieldset.column.right > div.window.bottom {
  height: 99% !important;
  width: 99% !important;
}

fieldset.column.left > div.window.top,
fieldset.column.left > div.window.bottom,
fieldset.column.right > div.window.top {
opacity:.2 !important ;
}

}
