css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("simoom.net") {
 * {
  color:#fff !important;
  background-color:#536a4d !important;
  text-shadow:none !important;
 }
 div#title h1:first-letter {
  color:#fff !important;
 }
 a:link    { color:#cfc !important; }
 a:visited { color:#cfc !important; }
 a:active  { color:#0f0 !important; }
 a:hover   { color:#ff6 !important; }

 .emps     { color:#9ba !important; }

 div#title {
  border-color:#9ba !important;
 }
 div#menu ul ul {
  border-top:2px solid #aba !important;
 }
 div#menu li li {
  border-bottom:1px solid #898 !important;
 }
 .col_bg {
  color:#536a4d !important;
 }
 .red,.red1,.red2 {
  color:#f99 !important;
 }
 .gray,.gray1,.gray2 {
  color:#ccc !important;
 }

 input, select, textarea {
  color:#333 !important;
  background-color:#fff !important;
  border:1px solid #333 !important;
 }
 input.btn {
  color:#333 !important;
  background-color:#fff !important;
  border:1px outset #333 !important;
 }
 .dimg {
  display:none !important;
 }
}
