css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("xbox360iso.com") {

/* Set background and text colors */
body, #recently, .page, .alt1, .alt2, .alt1Active { background: #000000 !important; color: #FFFFFF !important; }

/* Stylize and cleanup recent content */
.arrow-general { display: none; }
#scrollable_content { margin-top: 10px; padding-left: 10px; }
#recently { height: 250px; width: 920px !important; }
#recently-nav { margin-right: -15px !important; }
.items { height: 220px !important; }

/* Set link colors */
.alt1Active div a { color: #A4C9F5; }
a { color: #6BCEA1 !important; }
a:hover { color: #FF0000 !important; }
.tcat a:link, .tcat a:visited, .tcat_alink { color: #000000 !important; }

/* Remove most ads */
#header_adcode, #footer_adcode, #eXTReMe { display: none; }

}
