css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dokujunkies.org") {
/* re-layout main-area */
#wrap {
 position:absolute;
 left:20px;
 width:75% !important;
}

/* fix pade_desc */
#text { width: 70% !important }
#search_top { width: 30% !important }

#text,#search_top { padding: 0px !important }
#text p { padding-left: 20px !important }

/* use whole content-area */
#content_wrap {
 background-image: none !important;
 width: auto !important;
}
#content {
 width: 100% !important;
 overflow: hidden;
}

/* shift sidebar outside main-area */
#sidebar{
 position: absolute;
 top: 12px;
 left: 102%;
}

/* add some visual effects to sidebar */
#sidebar{
 background-color: rgba(255,255,255,0.98);
 border: 1px solid #000000;
 -moz-border-radius: 4px;
 -moz-box-shadow: 4px 4px 2px #000

}
}
