css:
/*

 * Gawker 2011 - Vintage Gawker (Removed Sidebar)

 *

 * Author      : tsunami643

 * Updated     : April 10, 2012

 * Description : Removes sidebar, widens what's left

 */


@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("kotaku.com"), 

domain("deadspin.com"), 

domain("lifehacker.com"),

domain("jezebel.com"), 

domain("gizmodo.com"), 

domain("io9.com"), 

domain("jalopnik.com"), 

domain("gawker.com"),

domain("fleshbot.com")

{
/*REMOVING THE SIDEBAR*/
#rightcontainer.cn_pager, #rightcontainer.ad_container, #rightbarSpinnerOverlay, #rightbarspinner, #right300spinner, #hoveringNextPostContainer, #gallery_transition_overlay, #fb_300x250, .cn_title, .sponsored_splash_post, .ad_marquee, .ad_chartbeat_end, a.tool.home, a.tool.popular, a.tool.search, #date-nav, #postTransitionOverlay, #post_list, #rightbar, #fbSiteLike, .icon.comment, #interstitial_bottom {display: none!important;}

#rightcontainer {right: -1000px!important;}

#rightbar_notification_container.inner {border-right: 0px solid #BBB!important;}


/*FIXING COMMENTS AND ARTICLE VIEW*/
.inner {width: 970px!important; padding-left: 10px!important; border-right: 1px solid #BBB!important; border-left: 1px solid #BBB!important;}

.gmgrid,
#classic_pager,
.ctext.clearfix,
.header,
.threadfooter,
.grid-full,
#comments
{width: 960px!important;}

.comment, #thread_navigation, #container {width: 960px!important;}

.topchrome {width: 960px!important;}

.thread,
.replies {width: 960px!important;}

.permalink .post-body.quickpost{width: 970px!important; min-height: 100px!important;}

.withpic .col-excerpt {width: 614px !important;}

.threadnav,
.thread_comments,
.comment.cid {width: 953px!important;}
.imgwrap.abovewrap {margin: -2px 0 13px 150px!important;}

#profile, .sitescontainer {width: 960px!important;}

/*FIXING GALLERY VIEW*/
.grid-bleed {margin-top: 2px!important;}
#thumb_list_window {width: 920px!important;}
#thumb_navigation {width: 964px!important;}
#gallery_container {width: 960px!important; height: 539px!important;}
.gallery_image {width: 960px !important; height: 539px!important;}
#gallery_container img, #gallery_video_container_2 object
{max-width: 960px !important; width: 960px !important; height: 539px !important; max-height: 539px !important; margin: auto !important;}
.permalink .lyteboxContainer .lyteboxLink a {text-indent: -139em!important;}
.permalink #gallery_container .lyteboxLink {bottom: 3px!important;}

/*REMOVING SISTER SITE LINKS*/
#sitetags {display:none !important;}
}
