css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Gawker Clean
   by derilium */

@-moz-document domain("lifehacker.com"),
               domain("gizmodo.com"),
               domain("gawker.com"),
               domain("deadspin.com"),
               domain("kotaku.com"),
               domain("jezebel.com"),
               domain("io9.com"),
               domain("jalopnik.com") {

  #rightcontainer {
    height:33px!important;
    position:absolute!important;
    top:-47px!important;
    left:-293px;
  }

  .cnpager,#pane,#rightbarSpinnerOverlay,iframe.fblike {
    display:none!important;
  }

  #main-container > div.inner, #comments .threadnav, #hoveringNextPostContainer {
    width:958px!important;
  }

   .comment, div.post-body {
    width:942px!important;
  }

  .chrome a.chromebut {
    border-right:0px!important;
  }
  
  .ctext {
    color:#000;
  }
}
