css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.okcupid.com")
{
	div#leaderboard_wrapper,
	div.section.matches,
	div#side_extras,
	div#right_side_bar,
	div#skyscraper_floater_wrapper,
	ul#browser_links,
	div.scrollbar,
	ul.thumbnails,
	div#recent_activity_block p>a.more,
	div#home_ad,
	div.okad,
	div#site_news,
	div.content_footer_wrapper
	{
		display:none;
	}

	div.photobrowser
	{
		-moz-box-shadow:none;
		-webkit-border-radius:0;
		-webkit-box-shadow:none;
		background-color:#FFF!important;
		border:none!important;
		border-radius:0;
		box-shadow:0;
		margin:0;
		padding:0 0 0 5px;
		width:775px;
	}

	div.photobrowser ul.items
	{
		float:none;
		width:auto;
	}

	div.photobrowser ul.items li.item
	{
		background-color:#EEE!important;
		margin:0 3px 3px 0 !important;
	}
}
