css:
@-moz-document domain('digg.com') {

body {
min-width: 890px;
}

div.diggadsriver { display: none; }

.header {
width: auto !important;
min-width: 0 !important;
max-width: none !important;
}

.pre-content,
#story-items
{
    width: auto !important;
}
.column-container
{
width: 100%;
}

.columns {
width: 100%;
margin-left: -320px;
}

.pre-content {
margin-bottom: 8px;
margin-left: 320px;
}

#left-column {
margin-top: 0;
}

#left-column {
height: 0;
margin-left: 320px;
}

#main-column {
float: none;
width: auto !important;
margin-left: 490px;
clear: none;
}

#main-column.permalink-column {
margin-left: 320px;
}

.ad {
display: none;
}


.comment-body {
line-height: 1.1em;
}

.comment-body br {
content: "";
display: block;
height: .5em;
}

.comment {
padding-top: 5px;
}

.comment-reply-options {
padding-bottom: 5px;
}

#permalink-story .story-item {
margin-bottom: 0;
}

.story-item {
padding: 5px 0;
}

}
