css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("notalwaysright.com") {

body
{
background-color: #1A1B1D !important;
}

html body div#rap div#left.column
{
width: 730px !important;
background-color: #1A1B1D !important;
color: #999 !important;
}

html body div#rap div#left.column .post
{
background: none !important;
}

html body div#rap div#navbar, html body div#rap div#left.column div#bulletin,
html body div#rap div#left.column .post div#jobstyle
{
display: none !important;
}

html body div#rap div#left.column .post h3.storytitle a
{
color: white !important;
}

html body div#rap div#left.column .post div.storycontent
{
line-height: 1.1em !important;
}

html body div#rap div#left.column .post
{
margin-bottom: -30px !important;
}

html body div#rap div#left.column script,
html body div#rap div#left.column noscript.noscript-show,
html body div#rap div#center.column div#menu
{
display: none !important;
}

}
