css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xgm.ru","http://xgm.ru/forum/index.php") {

.button {
	background: -moz-linear-gradient(#EBF2F9, #D2DCE5, #D2DCE5);
	color: black !important;
	-moz-border-radius: 4px !important;
	border:1px solid #214994 !important;
	font:10px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.button:hover {
	background: -moz-linear-gradient(#5c77a8, #3E5C92, #3E5C92);
	color: #EBF2F9 !important;
	-moz-border-radius: 4px !important;
	border:1px solid #214994 !important;
	font: 10px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.button:active {
	background: -moz-linear-gradient(#3962b0, #214994, #214994);
	color: #EBF2F9 !important;
	-moz-border-radius: 4px !important;
	border:1px solid #214994 !important;
	font: 10px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
input[type="checkbox"] {
	outline:1px solid #D2DCE5 !important;
}

.vbmenu_control{
background: -moz-linear-gradient(#194fb3, #284c8f, #2c426b) !important;
color: #EBF2F9 !important;
font: bold 11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important; white-space: nowrap !important;
}
.tcat{
background: -moz-linear-gradient(#b0c4de, #b0c4de, #8ca3c2) !important;
color: #EBF2F9 !important;
font: bold 11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important; white-space: nowrap !important;
color:black !important;
}
.vbmenu_control a:link,.vbmenu_control a:active,.vbmenu_control a:visited{
	color:#EBF2F9 !important;
	text-decoration: none !important;
}
.vbmenu_control a:hover{
	color:white !important;
	text-shadow: #EBF2F9 1px 1px 1px !important;
	text-decoration: none !important;
}

.txt2 blockquote {
background:#d3e3f0 !important;
color:#222222;
padding: 8px !important;
border:1px dashed black !important;
border-left:2px solid gray !important;
margin-top:4px !important;
}

.alt1 .smallfont div i {
color:#000ac7;
}
.alt1 .smallfont div b {
color:#4b0082;
}
.alt1 .smallfont div b i {
color:#000;
}
.alt1 .smallfont div a:link {
text-decoration:none !important;
}
.alt1 .smallfont div a:visited {
text-decoration:none !important;
}
.alt1 .smallfont div {
padding:2px 2px 2px 2px;
}

#ta_pagetext {width:100%;}
.reportlink {display:inline !important;}

}
