@-moz-document url-prefix('http://mlbpark.donga.com/mbs/'), url-prefix('http://mlbpark.donga.com/bbs/') {

#alpha_bullpen, #alpha_park, #all_wrapper_righttop,
html > body > table > tbody > tr > td > table:first-child,
html > body > table > tbody > tr > td > table:nth-child(2),
html > body > table > tbody > tr > td > table:nth-child(3),
html > body > table > tbody > tr:only-child,
html > body > table > tbody > tr > td:nth-child(4),
html > body > table > tbody > tr > td:nth-child(5),
html > body > table > tbody > tr > td:nth-child(6),
html > body > center > table,
[src*="overture"], [src*="http://ar.donga.com/RealMedia/ads"] { display: none; }
html > body > table > tbody > tr > td { text-align: left !important; }

