css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("gizmodo.com"), domain("lifehacker.com"), domain("kotaku.com"), domain("gawker.com"), domain("deadspin.com"), domain("jezebel.com"), domain("io9.com"), domain("jalopnik.com")

{
body{background:#333!important;color:white;}
a:visited{color:#F6C!important;}
.permalink h1.title a,.permalink h1.title, p, .ctext,h2 {color:white !important;}
#main-container > .inner, #rightcontainer {background:#333!important;}
.rightBarControl:hover{background:#555;}
#rightcontainer .post.current {background:#555!important;}
.chrome,.chrome a.chromebut.withlabel span,#date-nav,#rightcontainer .cn_pager,#rightcontainer .cn_pager.lower{background:#555;}
#date-nav .cn_title{color:inherit;}
.permalink blockquote {color:white!important;background-color:transparent!important;border-left:none 0 transparent !important;}
h1 a, h2 a, h3 a, h4 a, h5 a, h6 a {
color:inherit !important;
}


.ctext{font-size:13px!important;}
.ctext, #rightcontainer .post a,.permalink .post-body  {color: #fff!important;}
.thread,.thread .replies .comment  {border-bottom:1px solid #777;}
.thread .replies{margin-left: 25px!important;padding:0 0px 0 0px;background:#333!important;}
.comment {background:#333!important;color:white!important;}
.replies div.comment {width:590px !important;}
.comment .header .tools {width:auto;font-size:11px;}
.comment .header .tools a{color:#888;}
.comment .byline img.avatar{width:40px; height:40px;}
.comment .byline{ height:40px;}


#postTransitionOverlay{display:none!important;}




}
