css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("alphaomega.me") {

.min_padding {
margin: 0px 2px !important;
}

div#userinfo > ul#userinfo_username > form {
display: none !important;
}

div#userinfo > div:nth-child(3) {
display: none !important;
}

ul#userinfo_username {
position: static !important;
margin: 0 !important;
}

div#userinfo > ul#userinfo_major > center {
display: none !important;
}

ul#userinfo_major {
position: static !important;
margin: 0 !important;
margin-left: -10px !important;
}

ul#userinfo_stats, ul#userinfo_minor {
margin: 0 !important;
}

form#quickpostform > div:last-child {
margin-top: 2px !important;
}

div#content > div:first-child > h3 {
padding-top: 0.7em !important;
}

div#header > center > font:first-child {
display: none !important;
}

div#menu {
top: 80px !important;
}

div#logo {
margin-top: -5px !important;
}

div#userinfo {
margin-top: -1em !important;
}

div#content {
top: 45px !important;
}

div#header > h3:last-child {
display: none !important;
}

div#header > div:nth-child(4) {
display: none !important;
}

/*div#header > div:nth-child(5) {
display: none !important;
}*/

div#footer {
padding-bottom: 5px !important;
}

ul#userinfo_stats > li:nth-last-child(2) {
display: none !important;
}

div.thin {
width: 90% !important;
}

div.main_column2 {
width: 100% !important;
}

div.main_column2 > div {
margin-right: 250px !important;
}

div.sidebar > div.box {
margin-left:10px !important;
}

li#nav_rules {
display: none !important;
}

ul.bonus-list {
display:none !important;
}

li.bonus-show > span.time > img {
display:none !important;
}

div#header > div:nth-child(4) {
display: none !important;
}

div#content > h3 {
margin-left: 5em !important;
}

div#content > div {
margin: 0em 5em !important;
}

}
