css:
@-moz-document domain("acrimonie.com"), domain("cable6.net") {
/* Top */
body > div.navbar, body > div.adminbar, body > div.topbar, body > div.boardlist { top: 0; padding: 0.1em; position: fixed; background: inherit; z-index: 2; }
body > div.navbar { left: 0; text-align: left; }
body > div.adminbar, body > div.topbar, body > div.boardlist { right: 0; border-bottom: 1px solid gray; width: 100%; text-align: right; }
body > div.topbar { left:0 !important; text-align: left !important; }
body > div.logo { margin-top: 1em; }
body > div.fullhead { margin-top: 2.5em; }

/* Bottom */
body > table[border="1"], body > form#delform > table.userdelete { position: fixed; background-color: inherit; bottom: 0; padding: 0; border: none; }
body > table[border="1"] { left: 0; width: 100%; border-top: 1px solid gray; margin: 0; }
body > table[border="1"] > tbody > tr > td { border: none; float: left; padding: 0.1em; margin: 0; }
body > form#delform > table.userdelete { right: 0; z-index: 2; }
body > form#delform > table.userdelete br { display: none; }
body > div.boardlist.bottom { display: none; }
body > div.pages { position: fixed; bottom: 0; }
}
