css:
HTML, body   {background-color: transparent !important;
  background-image: -o-skin("Pagebar Transparent Skin") !important; color:#888!Important; }

table#programTable {background:rgba(0,0,0,0.7)!Important;border-radius:10px!Important;padding-bottom:10px!Important;margin-top:5%!Important;}

A {color:#888!Important}



div#iltalehtiFeed,div#stickyFooter,div#headerContainer,div.commentInput,div.userPictureContainer {display:none!important;}

#fogscreen {opacity:0.7!important;}

#programTable .time {background:#181818!Important;color:#CCC!Important;}

td.cellContainer{background:#111!Important}
table.programTableCell tbody tr:nth-child(even) {background:#181818!Important;}
#programTable .programTableCell .currentProgram .name,#programTable .programTableCell .currentProgram .name * {background:#333!Important;color:#FFF!Important;}
#programTable .cellContainer {border-left:1px solid #333!Important;border-top:1px solid #333!Important;border-bottom:1px solid #333!Important;}
#programTable .programTableRow .cellContainer:last-child {border-right:1px solid #333!Important;}
#programTable .favoriteRedBackground .programWrapper {background:#444!Important}

#programInformationWindowPage,#programActionBar {background:#111!Important;}
#programActionBar ul a:hover {background:#222!Important;}
.subPageHeader, #programMeta {color:#FFF!Important;}
#programInformationWindowPage #topComments .comment {background:#151515!Important;border:2px solid #111!Important;color:#fff!Important;}
#programInformationWindowPage .commentSection {background:#111!Important;}
#programInformationWindowPage #topComments .commentText {color:#888!important;}
