css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("burning-series.org") {
html {background:#333 !important;}
* {-moz-border-radius:0 !important;-moz-box-shadow:none !important;}
a {text-decoration:none !important;color:#1f2447 !important;}
a:hover {color:#687e8a !important;}
#root {font-family: sans-serif;background:#eef !important;}
#root > header,footer,#ad,#help {display:none !important;}

nav ul,
header,
.home li:nth-child(2n),
tr:nth-child(2n),
.pages li > a,
.home #shoutbox dl > dt {background:#ccc !important;color:#001 !important;}

nav li:hover,
nav ul a:hover,
.pages > li > a:hover {background:#aaa !important;color:#001 !important;}
}
