css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gizmodo.com"), domain("gawker.com"), domain("deadspin.com"), domain("kotaku.com"), domain("jezebel.com"), domain("io9.com"), domain("lifehacker.com"), domain("jalopnik.com"), domain("fleshbot.com") {
#main-container {
      width: auto !important;
      
   }   
   #main-container .inner, #container, #postform_widget, 
   .chrome a.chromebut.cn_nextpost, #hoveringNextPost span.headline,
   .gmgrid, .thread, .replies, .comment, .threadnav, 
   .form_part .header, .gmgrid .grid-full {
      width: auto !important;
   }
   #comments {
      width: auto !important;
      float: none !important;
      margin-right: 344px !important;
   }
   .mainContent .gmgrid {
      width: auto !important;
      margin-right: 344px !important;
   }
   .chrome {
      background-repeat: repeat-x !important;
   }
   #rightcontainer {
      margin-right: -344px;
   }
   #rightwrapper {
      width: 334px !important;
      right: 0px !important;
   }
   .topchrome {
      padding-right: 340px !important;
   }
   .sitescontainer {
      width: 100% !important;
      margin: 0 !important;
   }
   .bottomchrome {
      width: auto !important;
      right: 314px !important;
      left: 0px !important;
      margin: 0 !important;
   }
   .imgwrap.abovewrap {
      width: auto !important;
      text-align: center;
   }
   #postform_widget .form_part.pf_text {
      position: relative;
      width: auto !important;
      float: none !important;
      margin-right: 0px !important;
   }
   .threadnav.chrome {
      margin-right: -16px !important;
   }
   .pfc textarea {
      width: 100% !important;
   }
   .pfc.cn_expandedform {
      margin-right: 10px !important;
   }
   #postform_widget form .actions {
      float: none !important;
   }
   #postform_widget form .actions:after {
      content: ".";
      visibility: hidden;
      display: block;
      height: 0;
      clear: both;
   }
   #hoveringNextPostContainer a {
      padding-top: 0px !important;
   }
   #hoveringNextPostContainer a .headline {
      height: 14px;
      padding-top: 6px !important;
   }
}
