css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mediavida.com") {


.autor, .bautor {display:none;}
td .left a.under{display:none;}
.lastpost cite a.under{display:none;}
.lastpost cite:before{content:'Anónimo';display:inline !important;}
#memberinfo, #tab_fb, #tab_tw{display:none !important;}
div.post .msg{width:780px !important;}
div.post .msg .body .cuerpo{width:740px !important;}
.mola_wrap{display:none;}
.tinycol .tpanel{display:none;}
.tinycol a img{display:none;}
#meerkat-wrap, #pub{display:none !important;}
ul#visitas{display:none;}
#tooltip{display:none !important;}
.cuerpo .quote cite{display:none !important;}
.item img.icon{display:none !important;}
.item .stuff blockquote:first-child a:nth-of-type(odd){display:none;}
}
