css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain(boards.4chan.org) , domain(www.4chan.org) {

body[bgcolor], TABLE > TBODY > TR > TD.reply, TABLE > TBODY > TR > TD.replyhl, TABLE[id^="inline_quote_"] {
background-image:url(http://i.imgur.com/s2ek0.png) !important;
background-position:100% 100% !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important;
}
BODY > DIV.postarea {
opacity:0.8 !important
}
BODY > A[href="../imgboard.html"] + TABLE[width="100%"],
BODY > HR,
FORM[method="post"] > CENTER + HR,
TD.rules > LI, DIV.rules > LI,
TD.rules > DIV[style="position: relative;"] > DIV.rules:first-child + DIV + DIV,
DIV[style="position: relative;"]:last-child > DIV.rules:first-child + DIV[style="top: 0px; right: 0px; position: absolute;"],
BODY > DIV.postarea + HR + SCRIPT + CENTER + HR + CENTER,
DIV#AD {
display: none !important;
}
DIV.postarea {
padding:0 !important
}
}
