css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("community.secondlife.com") {


  /* hide text and overlay image when mouse is away */

  div.lia-badge-container .lia-badge-avatar-name-rank-container,
  div.lia-badge-container .lia-badge-community-url,
  div.lia-badge-container .lia-badge-statistics,
  div.lia-badge-container .lia-badge-user-registered-date-container,
  div.lia-badge-container .lia-badge-recent-posts
  {
    background-opacity: 0 !important;
  }

  div.lia-badge-member-container div.lia-user-rank,
  div.lia-badge-member-container div.lia-badge-statistics,
  div.lia-badge-member-container div.lia-badge-user-registered-date-container
  {
    opacity:0 !important;
  }

  div.lia-badge-member-container img.lia-user-rank-icon-left
  {
    opacity: 0 !important;
  }


  /* show text and overlay image when mouse is hivered over */

  div:hover.lia-badge-container .lia-badge-avatar-name-rank-container,
  div:hover.lia-badge-container .lia-badge-community-url,
  div:hover.lia-badge-container .lia-badge-statistics,
  div:hover.lia-badge-container .lia-badge-user-registered-date-container,
  div:hover.lia-badge-container .lia-badge-recent-posts
  {
    background-opacity:inherit !important;
  }

  div:hover.lia-badge-member-container div.lia-user-rank,
  div:hover.lia-badge-member-container div.lia-badge-statistics,
  div:hover.lia-badge-member-container div.lia-badge-user-registered-date-container
  {
    opacity:inherit !important;
  }

  div:hover.lia-badge-member-container img.lia-user-rank-icon-left
  {
    opacity:inherit !important;
  }
}
