css:
@-moz-document domain(twenga),
url-prefix( http://www.twenga.){
#new-header .top,
body{background:none !important;}
#global-container{border:none !important;}
.menu,
.logo .cb,
.home #col-left,
.home #col-right,
.search #col-right,
.search .summary-content,
#filters,
#ads-box-top,
#ads-box-bottom,
#content.search #header-summary,
.find-box,
.poll-box,
.TopBar{display:none !important;}
#search-bar .zoom,
#new-header #search_form .sbw{background:none !important;}
#content.search .zoom-image{left:230px !important;}
#new-header .sbw{-moz-border-radius:0 !important;border-width:5px !important;-moz-box-shadow:0 2px 3px #000;border-color:#16afdb !important;}
#search-bar strong.catch-line,
#search-bar p{color:#959595 !important;}
#new-header .top .logo{margin-top:33px !important;}
#content.search #partners-list,
#content.search #results-list li,
#partners-list ul li,
.search #col-left{width:960px !important;}
#content.search li .prd-image .bg{display:block !important;}
#new-header #search-bar .search{-moz-border-radius:0 !important;line-height:38px !important;height:38px !important;}
#search-bar{padding-bottom:0 !important;}
#new-header #search-bar .search:hover{background:#16afdb !important;}
#global-container .breadcrumb{padding-left:307px !important;}
#content.search li .prd-image{margin-left:120px;}
#content.search #partners-list{border:none !important;background:#e5e5e5 !important;}
#content.search #partners-list .title{font-size:11px !important;font-weight:bold;margin:0 !important;}
#survey,
#content.search .find-bar,
#pagination{width:981px !important;}
#content.search hr.clear{width:960px !important;}
#search-bar .catch-top{height:24px !important;}
}
