css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
Author: Thomas Jedenfelt, 2011-03-13
http://userstyles.org/users/117
Update: 2011-04-17

Style sheet name:
"FILESTUBE.COM - Cleaner Design"

W3C valid CSS Level 3.

Description:
Hides and positions various sections.
*/

@-moz-document domain("filestube.com") {

form.searchform {
  float: none !important;
  width: auto !important;
  margin-top: 1.5em !important;
  margin-left: 1em !important;
  }

.advancedsearcha {
  white-space: nowrap !important;
  }


#results > SCRIPT:first-child + B + A + BR + BR + DIV {
  background: #fcc !important;
  padding: .5em;
  }
#results > SCRIPT:first-child + DIV + B + A + BR + BR + DIV {
  background: #fc9 !important;
  padding: .5em;
  }
HTML > BODY > DIV:first-child + DIV > A:first-child + FORM + DIV + SCRIPT + DIV + BR + DIV + DIV + BR + DIV > DIV {
  background: #fc6 !important;
  padding: .5em;
  width: auto !important;
  }

#results_box {
  width: 70% !important;
  }

#results {
  width: 100% !important;
  }

div.book_sub {
  width: auto !important;
  }

div.book3 {
  background: #E1ECFE;
  position: absolute !important;
  margin-top: 1.2em !important;
  left: -9.4em !important;
  padding-right: 7em !important;
  }

div.book3 IMG[src="http://static.filestube.com/files/images/alert_small3.gif"] {
  font-weight: 100;
  position: relative !important;
  left: 37em !important;
  }

div.book3 a {
  font-weight: 100;
  position: relative !important;
  left: 37em !important;
  }

#results_left {
  margin-top: 2em !important;
  }

.homePgr {
  margin-top: 1em !important;
  float: none !important;
  width: auto !important;
  }

#top_options, .ac_res, .linkbox, .boxpromo,
#results > B, #results > A,
.resultadv2, .other_adv2, .fb_image,
HTML > BODY > DIV:last-child,
HTML > BODY > DIV:first-child + DIV > A:first-child + FORM + DIV + DIV + BR + DIV > BR:first-child + DIV,
HTML > BODY > DIV:first-child + DIV + DIV > A:first-child + FORM + DIV + DIV + BR + DIV > BR:first-child + DIV,
DIV.n_right_detail IFRAME {
  display: none !important;
  }

}
