@-moz-document url-prefix(http://www.vippix.com/share) {

/* Hides NoScript Place Holder Icons */
[class="__noscriptPlaceholder__ "] { display: none !important; }

/* Black Background */
table, td, th { background: #000000 !important; color: #ffffff !important; }

/* Hides White Border Around Image */
table, td, th { border: none !important; }

/* Hide iFrame Ads */
iframe { display: none !important; }

/* Hide "Header" */
.td_header, .td_header_right { display: none !important; }

/* Bottom Header Links */
.td_top_links { display: none !important; }

/* Page Formatting - Center Image For 1080p - Change To Correct Margin For Different Resolution */
#iimg {  margin-top: -10px !important; }

/* "Center" Ad */
HTML > BODY > CENTER > TABLE > TBODY > TR:nth-child(3) > TD > CENTER:nth-child(2) { display: none !important; }

/* Ad Below Image & Rating */
td.td_center center center { display: none !important; }

/* "Code Table" Below Image */
.code_table { display: none !important; }

/* Footer */
td.td_footer { display: none !important; }

}
