css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.leagueoflegends.com") {

.sca_not_agree .post_content,.sca_not_agree .message_footer,.sca_not_agree .avatar_top
{                -ms-filter:"progid:DXImageTransform.Microsoft.Alpha(Opacity=100)" !important;
                opacity:1.0 !important;

}

.sca_not_agree { color: black; }
.sca_not_agree table { background-color: #DDD !important; background-image: none !important; }
.sca_not_agree .avatar_top { background-image: none !important; }
.sca_not_agree td.left { background-color: #CCC !important; background-image: none !important; }
.sca_not_agree td.right { border-left: 1px solid #999 !important; }
.sca_not_agree div.message_header { border-bottom: 1px solid #999 !important; }
.sca_not_agree div.message_footer { border-top: 1px solid #999 !important; }
}
